
class Solution:
    def largestOddNumber(self, num: str) -> str:
        large_num='0'
        for i in range(len(num)):
            for j in range(i,len(num)):
                sub_num=num[i:j+1]
                if int(sub_num)%2!=0 and int(sub_num)>int(large_num):
                    large_num=sub_num

        return large_num

solution = Solution()
